onClick:
{{
  (async () => {
    const nome = InputNome.text?.trim();
    const email = InputEmail.text?.trim();
    const senha = InputSenha.text?.trim();
    const confirmar = InputConfirmarSenha.text?.trim();

    if (!nome || !email || !senha || !confirmar) {
      showAlert("Preencha todos os campos!", "warning");
      return;
    }

    if (senha !== confirmar) {
      showAlert("As senhas não coincidem!", "error");
      return;
    }

    await CriarUsuario.run()
      .then(() => {
        showAlert("Usuário criado com sucesso!", "success");
        closeModal("modalUsuario"); // substitua "modalUsuario" pelo nome REAL do seu modal
      })
      .catch((err) => {
        showAlert("Erro ao criar usuário: " + err.message, "error");
      });
  })();
}}
