defaultText:
{{
(() => {
  const totalOrcamento = getValorOrcamento.data?.data?.find(item => item.tipo === "geral")?.valor || 0;
  const totalBolsas = getMembros.data?.data
    ?.flatMap(m => m.planos_de_trabalho || [])
    ?.filter(p => p?.bolsa?.valor && p?.statusBolsa === "ATIVA")
    ?.reduce((total, p) => total + Number(p.bolsa.valor || 0), 0) || 0;

  const totalNotas = getNotas.data?.data
    ?.reduce((acc, nota) => acc + (Number(nota.valor_liquido) || 0), 0) || 0;

  const restanteTotal = totalOrcamento - totalBolsas - totalNotas;

  return restanteTotal.toLocaleString('pt-BR', {
    style: 'currency',
    currency: 'BRL'
  });
})()
}}
