onClick:
{{
  LogarUsuario.run()
    .then(() => {
      const dados = LogarUsuario.data?.data?.[0];  // <- aqui foi corrigido
      const emailDigitado = InputEmail.text?.trim().toLowerCase();
      const senhaDigitada = InputSenha.text?.trim();

      if (!dados) {
        throw new Error("Usuário não encontrado.");
      }

      const emailBanco = dados.email?.trim().toLowerCase();
      const senhaBanco = dados.senha?.trim();

      if (emailDigitado !== emailBanco) {
        throw new Error("Email incorreto.");
      }

      if (senhaDigitada !== senhaBanco) {
        throw new Error("Senha incorreta.");
      }

      storeValue("usuario", dados);
      showAlert("Login realizado com sucesso", "success");

      setTimeout(() => {
        navigateTo("Home Page", "SAME_WINDOW");
      }, 300);
    })
    .catch((err) => {
      showAlert("Erro: " + err.message, "error");
    });
}}
