onClick:
{{

postItemFluxoCaixa.run({
  data: {
    descricao: InputDescricao.text,
    receita: Number(InputReceita.text.replace(",", ".")) || 0,
    despesa: Number(InputDespesa.text.replace(",", ".")) || 0,
    contacorrente: Number(ContaCorrente.text.replace(",", ".")) || 0,
    data: SelecionarData.selectedDate,
    situacao: InputSituacao.text || "Executado",
    capital_custeio_item: {
      connect: [
        {
          documentId: appsmith.store.documentoDoItemSelecionado
        }
      ]
    }
  }
}, () => {
  closeModal('Modal1');
  getItensFluxoCaixa.run(); // Atualiza a tabela apÃ³s salvar
  showAlert("Item adicionado com sucesso!", "success");
}, (err) => {
  showAlert("Erro ao adicionar item: " + err.message, "error");
});


}}
