{{
(() => {
  const itens = getCapitalCusteioItens.data?.data || [];
  const movimentos = getItensDoFluxoCompleto.data?.data || [];

  const totalRestante = itens.reduce((soma, item) => {
    const total = Number(item.despesa || 0);

    const gasto = movimentos
      .filter(mov => mov.capital_custeio_item?.documentId === item.documentId)
      .reduce((acc, mov) => acc + Math.abs(Number(mov.despesa || 0)), 0);

    const restante = total - gasto;

    return soma + restante;
  }, 0);

  return totalRestante.toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL"
  });
})()
}}
