{{
(() => {
  const projetos = getProjetos.data?.data || [];
  const membros = getMembros.data?.data || [];

  const totalProjetos = projetos.length;

  const totalProjetosComBolsas = new Set(
    membros.flatMap(m => m.planos_de_trabalho || [])
      .filter(p => p.bolsa?.valor && p.statusBolsa === "ATIVA")
      .flatMap(p => p.projetos || [])
      .map(p => p.documentId)
  ).size;

  const membrosTotal = membros.length;

  const membrosComAlocacao = membros.filter(m => 
    (m.planos_de_trabalho || []).some(p => (p.projetos || []).length > 0)
  ).length;

  const membrosSemAlocacao = membrosTotal - membrosComAlocacao;

  const totalAlocacoes = membros.flatMap(m => m.planos_de_trabalho || [])
    .filter(p => p.projetos?.length).length;

  const bolsasAtivas = membros.flatMap(m => m.planos_de_trabalho || [])
    .filter(p => p.statusBolsa === "ATIVA" && p.bolsa?.valor).length;

  const valorTotalBolsas = membros.flatMap(m => m.planos_de_trabalho || [])
    .filter(p => p.statusBolsa === "ATIVA" && p.bolsa?.valor)
    .reduce((acc, p) => acc + Number(p.bolsa.valor || 0), 0)
    .toLocaleString("pt-BR", { style: "currency", currency: "BRL" });

  const equipes = new Set(
    membros.flatMap(m => m.equipes || []).map(e => e.nome)
  );

  return `
Projetos cadastrados: ${totalProjetos}
Projetos com bolsas: ${totalProjetosComBolsas}

Membros cadastrados: ${membrosTotal}
Membros com alocação: ${membrosComAlocacao}
Membros sem alocação: ${membrosSemAlocacao}

Alocações em projetos: ${totalAlocacoes}
Bolsas ativas: ${bolsasAtivas}
Valor total de bolsas: ${valorTotalBolsas}

Equipes (${equipes.size}): ${[...equipes].join(", ") || "-"}`.trim();
})()
}}
