{{
(() => {
  const membros = getMembros.data?.data || [];

  const equipesResumo = {};

  membros.forEach(membro => {
    const valorBolsas = (membro.planos_de_trabalho || [])
      .reduce((soma, plano) => soma + Number(plano?.bolsa?.valor || 0), 0);

    const equipes = membro.equipes || [];

    equipes.forEach(eq => {
      const nomeEquipe = eq.nome || "Sem equipe";

      if (!equipesResumo[nomeEquipe]) {
        equipesResumo[nomeEquipe] = { membros: 0, gasto: 0 };
      }

      equipesResumo[nomeEquipe].membros += 1;
      equipesResumo[nomeEquipe].gasto += valorBolsas;
    });
  });

  return Object.entries(equipesResumo)
    .sort((a, b) => b[1].gasto - a[1].gasto)  // ordena por maior gasto
    .slice(0, 3)  // mostra sÃ³ as 3 primeiras
    .map(([nome, info]) =>
      `Equipe: ${nome}\n- Membros: ${info.membros}\n- Gasto total: ${info.gasto.toLocaleString('pt-BR', {
        style: 'currency',
        currency: 'BRL'
      })}\n`
    )
    .join('\n');
})()
}}
