{{
(() => {
  const dados = getItensFluxoCaixa.data?.data || [];

  const receitaTotal = dados.reduce((soma, item) => {
    const valor = Number(item.receita);
    return soma + (isNaN(valor) ? 0 : valor);
  }, 0);

  const despesaTotal = dados.reduce((soma, item) => {
    const valor = Number(item.despesa);
    return soma + (isNaN(valor) ? 0 : valor);
  }, 0);

  const contaCorrente = receitaTotal + despesaTotal;

  return contaCorrente.toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL"
  });
})()
}}
