onClick:
{{
  SelectBolsa.selectedOptionValue &&
  SelectProject.selectedOptionValue &&
  DatePicker1.selectedDate &&
  DatePicker2.selectedDate &&
  Table1.selectedRow?.documentId &&
  Financiador.selectedOptionValue
  ? getMembrosByDocumentId.run(
      () => {
        const membroData = getMembrosByDocumentId.data?.data?.[0];

        if (membroData && membroData.id) {
          storeValue("idInternoMembro", membroData.id);
          addPlanoDeTrabalho.run(
            () => {
              showAlert("Plano de trabalho adicionado com sucesso", "success");
              closeModal("Adicionar_bolsa");
            },
            (err) => {
              showAlert("Erro ao adicionar plano de trabalho: " + err.message, "error");
            }
          );
        } else {
          showAlert("Membro não encontrado a partir do documentId", "error");
        }
      },
      (err) => {
        showAlert("Erro ao buscar membro por documentId: " + err.message, "error");
      }
    )
  : showAlert("Preencha todos os campos obrigatórios antes de confirmar", "error");
}}
